                                    <span onClick={() => this.deleteItem(index)} className="fa fa-trash"></span>
                                </div>
                                <div className="d-block">
                                    <strong>Due Date:</strong> <span>{item.dueDate.toLocaleDateString()}</span>
                                </div>
                            </div>
    }
}

export default ToDoList;
